package fr.luna.fusionplugin.Player;import org.bukkit.attribute.Attribute;import org.bukkit.entity.Player;import java.util.UUID;public class PlayerStats {    public int basehp = 20;    public int basestr = 2;    public double basespeed = 0.1D;    public int basearmor = 0;    private int healthlvl;    private double healthstat = 1;    private int strengthlvl;    private double strengthstat = 1;    private UUID uuid;    private int speedlvl;    private double speedstat = 0.015;    private int armorlvl;    private double armorstat = 1;    public void updateStats(Player p, PlayerStats ps){        p.getAttribute(Attribute.GENERIC_ATTACK_DAMAGE).setBaseValue(basestr + (ps.getStrengthlvl() * strengthstat));        p.getAttribute(Attribute.GENERIC_MAX_HEALTH).setBaseValue(basehp + (ps.getHealthlvl() * healthstat));        p.getAttribute(Attribute.GENERIC_ARMOR).setBaseValue(basearmor + (ps.getArmorlvl() * armorstat));        p.getAttribute(Attribute.GENERIC_MOVEMENT_SPEED).setBaseValue(basespeed + (ps.getSpeedlvl() * speedstat));        PlayerUtility.setPlayerStats(p,this);    }    public void setHealthlvl(int healthlvl){        this.healthlvl = healthlvl;    }    public int getHealthlvl(){        return healthlvl;    }    public void setStrengthlvl(int strengthlvl){        this.strengthlvl = strengthlvl;    }    public int getStrengthlvl(){        return strengthlvl;    }    public void setUuid(UUID uuid){        this.uuid = uuid;    }    public UUID getUuid(){        return uuid;    }    public void setSpeedlvl(int speedlvl){        this.speedlvl = speedlvl;    }    public int getSpeedlvl(){        return speedlvl;    }    public void setArmorlvl(int armorlvl){        this.armorlvl = armorlvl;    }    public int getArmorlvl(){        return armorlvl;    }    public double getStrengthstat() {return strengthstat;}    public double getHealthstat() {return healthstat;}    public double getSpeedstat() {return speedstat;}    public double getArmorstat() {return armorstat;}}