package fr.luna.fusionplugin.Player;import org.bukkit.attribute.Attribute;import org.bukkit.entity.Player;import java.util.UUID;public class PlayerStats {    private double health;    private double strength;    private UUID uuid;    private double speed;    private double resistance;    public void updateStats(Player p, PlayerStats ps){        p.getAttribute(Attribute.GENERIC_ATTACK_DAMAGE).setBaseValue(ps.getStrength());        p.getAttribute(Attribute.GENERIC_MAX_HEALTH).setBaseValue(ps.getHealth());        p.getAttribute(Attribute.GENERIC_ARMOR).setBaseValue(ps.getResistance());        p.getAttribute(Attribute.GENERIC_MOVEMENT_SPEED).setBaseValue(ps.getSpeed());        PlayerUtility.setPlayerStats(p,this);    }    public void setHealth(double health){        this.health = health;    }    public double getHealth(){        return health;    }    public void setStrength(double strength){        this.strength = strength;    }    public double getStrength(){        return strength;    }    public void setUuid(UUID uuid){        this.uuid = uuid;    }    public UUID getUuid(){        return uuid;    }    public void setSpeed(double speed){        this.speed = speed;    }    public double getSpeed(){        return speed;    }    public void setResistance(double resistance){        this.resistance = resistance;    }    public double getResistance(){        return resistance;    }}