package fr.luna.fusionplugin;import org.bukkit.Bukkit;import org.bukkit.attribute.Attribute;import org.bukkit.entity.Player;import java.util.UUID;public class PlayerStats {    private double health;    private double strength;    private UUID uuid;    private double speed;    private double resistance;    public void updateStats(Player p){        this.health = p.getAttribute(Attribute.GENERIC_MAX_HEALTH).getValue();        this.strength = p.getAttribute(Attribute.GENERIC_ATTACK_DAMAGE).getValue();        this.speed = p.getAttribute(Attribute.GENERIC_MOVEMENT_SPEED).getValue();        this.resistance = p.getAttribute(Attribute.GENERIC_ARMOR).getValue();    }    public void setHealth(double health){        this.health = health;    }    public double getHealth(){        return health;    }    public void setStrength(double strength){        this.strength = strength;    }    public double getStrength(){        return strength;    }    public void setUuid(UUID uuid){        this.uuid = uuid;    }    public UUID getUuid(){        return uuid;    }    public void setSpeed(double speed){        this.speed = speed;    }    public double getSpeed(){        return speed;    }    public void setResistance(double resistance){        this.resistance = resistance;    }    public double getResistance(){        return resistance;    }}